mapper.select:
select id, publisher_id, title, content, publish_time, update_time,status
from openexperiment.announcement
where id = #{id,jdbcType=BIGINT}

  and status = #{status}

select id, title, publish_time,status
from openexperiment.announcement


    college = #{college}


    publish_time >= #{startTime}


    and publish_time <= #{endTime}


    and status= #{status}


    and locate(#{title},title)


order by publish_time
desc

select id, title, publish_time,status
from openexperiment.announcement


    college = #{college}


    and status= #{status}


order by publish_time
desc

select id, title, publish_time,status
from openexperiment.announcement

    college IS NULL

    publish_time >= #{startTime}


    and publish_time <= #{endTime}


    and status= #{status}


    and locate(#{title},title)


order by publish_time
desc
