mapper.insert:
insert into major (id, major_code, major_name
  )
values (#{id,jdbcType=INTEGER}, #{majorCode,jdbcType=VARCHAR}, #{majorName,jdbcType=VARCHAR}
  )

mapper.update:
update major
set major_code = #{majorCode,jdbcType=VARCHAR},
  major_name = #{majorName,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, major_code, major_name
from major
where id = #{id,jdbcType=INTEGER}
