mapper.select:
select openexperiment.user_project_group.project_group_id as id, openexperiment.project_group.address, openexperiment.project_group.serial_number, openexperiment.project_group.create_time, openexperiment.project_group.creator_id, openexperiment.project_group.end_time, openexperiment.project_group.experiment_condition, openexperiment.project_group.suggest_group_type, openexperiment.project_group.experiment_type, openexperiment.project_group.achievement_check, openexperiment.project_group.limit_college, openexperiment.project_group.limit_major, openexperiment.project_group.limit_grade, openexperiment.project_group.fit_people_num, openexperiment.project_group.total_hours, openexperiment.project_group.lab_name, openexperiment.project_group.project_name, openexperiment.project_group.project_type, openexperiment.project_group.start_time, openexperiment.project_group.`status`, openexperiment.project_group.update_time, openexperiment.project_group.main_Content, openexperiment.project_group.is_open_topic from openexperiment.project_group inner join openexperiment.user_project_group on user_project_group.project_group_id = openexperiment.project_group.id
user_project_group.user_id = #{userId}
and openexperiment.project_group.`status` = #{projectStatus}

select openexperiment.project_group.limit_college, openexperiment.project_group.is_open_topic, openexperiment.project_group.serial_number, openexperiment.project_group.id, openexperiment.project_group.project_name, openexperiment.project_group.experiment_type, openexperiment.project_group.total_hours, openexperiment.project_group.start_time, openexperiment.project_group.end_time, openexperiment.project_group.lab_name, openexperiment.project_group.address, openexperiment.project_group.subordinate_college, openexperiment.project_group.suggest_group_type, openexperiment.project_group.project_type, openexperiment.project_group.main_Content, openexperiment.project_group.apply_funds from openexperiment.project_group where status = #{projectStatus}
and project_type = #{projectType}
order by create_time desc

select count(id) from  openexperiment.project_group where id in

    #{id}

and `status` = #{status}

select project_group.id
    from openexperiment.project_group


        project_group.status = #{status}


        and project_group.subordinate_college = #{college}


        and project_group.suggest_group_type = #{suggestGroupType}


        and project_group.experiment_type = #{experimentType}


        and project_group.start_time >= #{startTime}


        and project_group.end_time <= #{endTime}



        and locate(#{limitMajor},project_group.limit_major)


        and locate(#{limitCollege},project_group.limit_college)


        and locate(#{limitGrade},project_group.limit_grade)


        and locate(#{projectName},project_group.project_name)

select project_group.id
from openexperiment.project_group inner join openexperiment.key_project_status
    on project_group.id = key_project_status.project_id


        project_group.status = #{status}


        and project_group.subordinate_college = #{college}


        and project_group.suggest_group_type = #{suggestGroupType}


        and project_group.experiment_type = #{experimentType}


        and project_group.start_time >= #{startTime}


        and project_group.end_time <= #{endTime}



        and locate(#{limitMajor},project_group.limit_major)


        and locate(#{limitCollege},project_group.limit_college)


        and locate(#{limitGrade},project_group.limit_grade)


        and locate(#{projectName},project_group.project_name)
