mapper.update:
update need_project_review
set college = #{college,jdbcType=INTEGER},
  project_type = #{projectType,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, college, project_type
from need_project_review
where id = #{id,jdbcType=INTEGER}

mapper.update:
UPDATE project_group set project_group.status = -8
where project_group.status = 4 and project_group.subordinate_college = #{college}
and project_group.project_type = 1

UPDATE project_group set project_group.status = 3
where project_group.status = -8 and project_group.subordinate_college = #{college}
and project_group.project_type = 1
