mapper.update:
update project_review
set project_id = #{projectId,jdbcType=BIGINT},
  score = #{score,jdbcType=INTEGER},
  is_support = #{isSupport,jdbcType=BIT},
  operate_user = #{operateUser,jdbcType=BIGINT}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, project_id, score, is_support, operate_user
from project_review
where id = #{id,jdbcType=INTEGER}
