mapper.insert:
insert into function_gives_grade (project_id, grade,
  acceptance_time, project_type, operator_name
  )
values (#{projectId,jdbcType=BIGINT}, #{grade,jdbcType=INTEGER},
  #{acceptanceTime,jdbcType=TIMESTAMP}, #{projectType,jdbcType=INTEGER}, #{operatorName,jdbcType=VARCHAR}
  )

mapper.select:
select id, project_id, grade, acceptance_time, project_type, operator_name
from function_gives_grade
where id = #{id,jdbcType=BIGINT}
