mapper.update:
update news_release
set
  title = #{title,jdbcType=VARCHAR},
  content = #{content,jdbcType=LONGVARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, real_name, title, publish_time, update_time, `status`, content
from homepage_announcement
where id = #{id,jdbcType=INTEGER}
