mapper.update:
update receive_notice
set notice_id = #{noticeId,jdbcType=BIGINT},
  receiver_id = #{receiverId,jdbcType=BIGINT},
  `status` = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, notice_id, receiver_id, `status`
from receive_notice
where id = #{id,jdbcType=BIGINT}
